import React, { useState } from "react";
import axios from "axios";
import './styles.css'; // Import the CSS file for styles

const Summarizer = () => {
  const [text, setText] = useState(""); // Input text
  const [summary, setSummary] = useState(""); // Summarized output
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state

  const handleSummarize = async () => {
    setLoading(true);
    setSummary(""); // Clear previous summary
    setError(""); // Clear previous error

    try {
      // Send the text to the Flask backend for summarization
      const response = await axios.post("http://127.0.0.1:5000/summarize", {
        text,
      });

      // Set the summary in the state
      setSummary(response.data.summary);
    } catch (error) {
      console.error("Error during summarization:", error);
      setError("An error occurred while summarizing the text.");
    }

    setLoading(false);
  };

  return (
    <div className="fullscreen-container">
      <div className="summarizer-card">
        <h1>Text Summarizer</h1>

        <textarea
          className="textarea"
          rows="6"
          placeholder="Enter text to summarize..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        ></textarea>

        <button
          className="button"
          onClick={handleSummarize}
          disabled={loading || !text.trim()}
        >
          {loading ? (
            <div className="spinner"></div>
          ) : (
            "Summarize"
          )}
        </button>

        {summary && (
          <div className="result-box">
            <h3>Summary:</h3>
            <p>{summary}</p>
          </div>
        )}

        {error && (
          <div className="result-box error-box">
            <h3>Error:</h3>
            <p>{error}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Summarizer;
