import React, { useState } from "react";
import axios from "axios";

const Summarizer = () => {
  const [text, setText] = useState(""); // Input text
  const [summary, setSummary] = useState(""); // Summarized output
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state

  const handleSummarize = async () => {
    setLoading(true);
    setSummary(""); // Clear previous summary
    setError(""); // Clear previous error

    try {
      // Send the text to the Flask backend for summarization
      const response = await axios.post("http://127.0.0.1:5000/summarize", {
        text,
      });

      // Set the summary in the state
      setSummary(response.data.summary);
    } catch (error) {
      console.error("Error during summarization:", error);
      setError("An error occurred while summarizing the text.");
    }

    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 flex items-center justify-center">
      <div className="max-w-lg w-full p-6 bg-white rounded-lg shadow-2xl">
        <h1 className="text-3xl font-semibold text-center text-gray-800 mb-6">
          Text Summarizer
        </h1>

        <textarea
          className="w-full p-4 mb-6 border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-400 focus:outline-none transition duration-300 ease-in-out transform hover:scale-105"
          rows="6"
          placeholder="Enter text to summarize..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        ></textarea>

        <div className="text-center">
          <button
            className={`bg-indigo-600 text-white px-6 py-3 rounded-lg font-medium transition duration-300 ease-in-out transform ${
              loading ? "opacity-50 cursor-not-allowed" : "hover:scale-105"
            }`}
            onClick={handleSummarize}
            disabled={loading || !text.trim()}
          >
            {loading ? (
              <span className="animate-spin">ðŸ”„ Summarizing...</span>
            ) : (
              "Summarize"
            )}
          </button>
        </div>

        {summary && (
          <div className="mt-8 p-6 border-l-4 border-indigo-600 bg-indigo-50 rounded-lg">
            <h3 className="text-xl font-semibold text-indigo-700">Summary:</h3>
            <p className="text-gray-700">{summary}</p>
          </div>
        )}

        {error && (
          <div className="mt-4 p-6 border-l-4 border-red-600 bg-red-50 rounded-lg">
            <h3 className="text-xl font-semibold text-red-700">Error:</h3>
            <p className="text-gray-700">{error}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Summarizer;
