import React, { useState } from "react";
import { pipeline } from "@xenova/transformers";

const Summarizer = () => {
  const [text, setText] = useState(""); // Input text
  const [summary, setSummary] = useState(""); // Summarized output
  const [loading, setLoading] = useState(false); // Loading state

  const handleSummarize = async () => {
    setLoading(true);
    setSummary(""); // Clear previous summary

    try {
      // Load the summarization pipeline
      const summarizer = await pipeline("summarization", "Xenova/bart-large-cnn");

      // Generate the summary
      const result = await summarizer(text, { max_length: 50, min_length: 25 });
      setSummary(result[0].summary_text);
    } catch (error) {
      console.error("Error during summarization:", error);
      setSummary("An error occurred while summarizing the text.");
    }

    setLoading(false);
  };

  return (
    <div className="container mx-auto p-4 max-w-xl">
      <h1 className="text-2xl font-bold text-center mb-4">Text Summarizer</h1>
      <textarea
        className="w-full p-2 border rounded mb-4"
        rows="5"
        placeholder="Enter text to summarize..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      ></textarea>
      <button
        className="bg-blue-500 text-white px-4 py-2 rounded"
        onClick={handleSummarize}
        disabled={loading || !text.trim()}
      >
        {loading ? "Summarizing..." : "Summarize"}
      </button>
      {summary && (
        <div className="mt-4 p-4 border rounded bg-gray-100">
          <h3 className="font-bold">Summary:</h3>
          <p>{summary}</p>
        </div>
      )}
    </div>
  );
};

export default Summarizer;
