import React, { useState } from "react";
import axios from "axios";
import "./styles.css"; // Importing custom CSS file

const Summarizer = () => {
  const [text, setText] = useState(""); // Input text
  const [summary, setSummary] = useState(""); // Summarized output
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state
  const [isDarkMode, setIsDarkMode] = useState(false); // Dark mode toggle

  const handleSummarize = async () => {
    setLoading(true);
    setSummary(""); // Clear previous summary
    setError(""); // Clear previous error

    try {
      // Send the text to the Flask backend for summarization
      const response = await axios.post("http://127.0.0.1:5000/summarize", {
        text,
      });

      // Set the summary in the state
      setSummary(response.data.summary);
    } catch (error) {
      console.error("Error during summarization:", error);
      setError("An error occurred while summarizing the text.");
    }

    setLoading(false);
  };

  return (
    <div
      className={`min-h-screen flex flex-col justify-center items-center ${isDarkMode ? "dark" : ""} transition-all duration-500 ease-in-out`}
    >
      <div className="w-full max-w-2xl px-6 py-8 bg-white dark:bg-gray-900 rounded-xl shadow-xl shadow-gray-500/20 transition-all duration-500 ease-in-out">
        <div className="text-center mb-8">
          <h1 className="text-5xl font-extrabold text-gray-800 dark:text-white mb-6">
            CUMIN: A Text Summarizer
          </h1>
          <button
            className="py-2 px-6 mb-6 bg-gradient-to-r from-purple-400 to-pink-500 text-white font-semibold rounded-full shadow-lg hover:scale-105 transform transition-all duration-500 ease-in-out"
            onClick={() => setIsDarkMode(!isDarkMode)}
          >
            Toggle {isDarkMode ? "Light" : "Dark"} Mode
          </button>
        </div>

        <textarea
          className="w-full p-6 border-2 border-gray-300 dark:border-gray-600 mb-4 text-lg rounded-xl shadow-lg focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-offset-2 transition-all ease-in-out"
          rows="6"
          placeholder="Enter text to summarize..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        ></textarea>

        <button
          className="w-full py-3 bg-gradient-to-r from-indigo-500 to-blue-600 text-white font-semibold rounded-lg disabled:opacity-50 shadow-md hover:opacity-90 transform transition-all duration-300"
          onClick={handleSummarize}
          disabled={loading || !text.trim()}
        >
          {loading ? "Summarizing..." : "Summarize"}
        </button>

        {summary && (
          <div className="mt-8 p-6 bg-white dark:bg-gray-800 text-gray-800 dark:text-white rounded-xl shadow-xl shadow-gray-500/20">
            <h3 className="font-bold text-2xl text-blue-500">Summary:</h3>
            <p>{summary}</p>
          </div>
        )}

        {error && (
          <div className="mt-8 p-6 bg-red-100 dark:bg-red-600 text-red-600 dark:text-white rounded-xl shadow-xl shadow-gray-500/20">
            <p>{error}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Summarizer;
