import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./styles.css"; // Importing custom CSS file

const Summarizer = () => {
  const [text, setText] = useState(""); // Input text
  const [summary, setSummary] = useState(""); // Summarized output
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state
  const [isDarkMode, setIsDarkMode] = useState(false); // Dark mode toggle
  const [feedback, setFeedback] = useState(""); // Feedback state
  const [submitted, setSubmitted] = useState(false); // Feedback submission state
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is authenticated
    const isAuthenticated = localStorage.getItem("isAuthenticated");
    if (!isAuthenticated || isAuthenticated !== "true") {
      navigate("/");
    }
  }, [navigate]);

  const handleSummarize = async () => {
    setLoading(true);
    setSummary(""); // Clear previous summary
    setError(""); // Clear previous error

    try {
      // Send the text to the Flask backend for summarization
      const response = await axios.post("http://127.0.0.1:5000/summarize", {
        text,
      });

      // Set the summary in the state
      setSummary(response.data.summary);
    } catch (error) {
      console.error("Error during summarization:", error);
      setError("An error occurred while summarizing the text.");
    }

    setLoading(false);
  };

  const handleFeedbackSubmit = (e) => {
    e.preventDefault();

    if (feedback.trim()) {
      console.log("Feedback submitted:", feedback); // Replace with backend call
      setSubmitted(true);
      setFeedback("");
      setTimeout(() => {
        navigate("/summarizer"); // Optionally, redirect after feedback submission
      }, 1500);
    } else {
      alert("Please enter your feedback before submitting.");
    }
  };

  return (
    <div className={`min-h-screen flex flex-col justify-center items-center ${isDarkMode ? "dark" : ""}`}>
      <div className="w-full max-w-2xl px-4 py-8">
        <div className="text-center">
          <h1 className="text-4xl font-bold mb-4">CUMIN a Text Summarizer</h1>
          <button
            className="mb-8 py-2 px-4 bg-gradient-to-r from-green-400 via-blue-500 to-purple-600 text-white rounded-full"
            onClick={() => setIsDarkMode(!isDarkMode)}
          >
            Toggle {isDarkMode ? "Light" : "Dark"} Mode
          </button>
        </div>

        <textarea
          className="w-full p-4 border-2 border-gray-300 dark:border-gray-600 mb-4 resize-none text-lg rounded-lg shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="6"
          placeholder="Enter text to summarize..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        ></textarea>

        <button
          className="w-full py-3 bg-gradient-to-r from-blue-500 to-indigo-600 text-white font-semibold rounded-lg disabled:opacity-50 shadow-md hover:opacity-80"
          onClick={handleSummarize}
          disabled={loading || !text.trim()}
        >
          {loading ? "Summarizing..." : "Summarize"}
        </button>

        {summary && (
          <div className="mt-6 p-6 bg-white dark:bg-gray-800 text-gray-800 dark:text-white rounded-lg shadow-md">
            <h3 className="font-bold text-xl text-blue-500">Summary:</h3>
            <p>{summary}</p>
          </div>
        )}

        {error && (
          <div className="mt-6 p-6 bg-red-100 dark:bg-red-600 text-red-600 dark:text-white rounded-lg shadow-md">
            <p>{error}</p>
          </div>
        )}

        {/* Feedback Form */}
        {!submitted && (
          <div className="mt-8 p-6 bg-white dark:bg-gray-800 text-gray-800 dark:text-white rounded-lg shadow-md">
            <h3 className="font-bold text-xl text-blue-500 mb-4">Feedback</h3>
            <form onSubmit={handleFeedbackSubmit}>
              <textarea
                className="w-full p-4 border-2 border-gray-300 dark:border-gray-600 mb-4 resize-none text-lg rounded-lg shadow-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                rows="4"
                placeholder="Enter your feedback here..."
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></textarea>
              <button
                type="submit"
                className="w-full py-3 bg-gradient-to-r from-green-400 via-blue-500 to-purple-600 text-white font-semibold rounded-lg disabled:opacity-50 shadow-md hover:opacity-80"
              >
                Submit Feedback
              </button>
            </form>
          </div>
        )}

        {submitted && (
          <div className="mt-8 p-6 bg-green-100 dark:bg-green-600 text-green-600 dark:text-white rounded-lg shadow-md">
            <p>Thank you for your feedback!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Summarizer;
